import React from "react";
import styled from "styled-components";
import HeroImg from "../assets/heroIllustration.svg";
import ActionButton from "../components/ActionButton";

const Section = styled.div`
  min-height: ${(props) => `calc(100vh - ${props.theme.navHeight})`};
  background-color: ${(props) => props.theme.body};
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  @media (max-width: 768px) {
    min-height: 100vh;
  }
`;
const Container = styled.div`
  width: 80%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;
const RightBox = styled.div`
  width: 40%;
  display: flex;
  align-items: center;
  justify-content: flex-end;
`;
const HeroImageContainer = styled.img`
  max-width: 100%;
  object-fit: cover;
  @media (max-width: 768px) {
    width: auto;
  }
`;
const LeftBox = styled.div`
  width: 40%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
`;
const Heading = styled.h1`
  width: 100%;
  margin-bottom: 0.8rem;
`;
const SubHeading = styled.h2`
  width: 100%;
  margin-bottom: 0.7px;
`;
const Paragraph = styled.p`
  width: 100%;
  margin-bottom: 0.6px;
`;

const HomePage = () => {
  return (
    <Section>
      <Container>
        <LeftBox>
          <Heading>Grow your business with our e-commerce solution</Heading>
          <SubHeading>Get more sales and better Inventory Managment</SubHeading>
          <Paragraph>
            {" "}
            Contact us to get Myhanuut and explore a lot of possibilities and be
            be a part of endless promising features
          </Paragraph>
          <ActionButton>Download</ActionButton>
        </LeftBox>
        <RightBox>
          <HeroImageContainer src={HeroImg} />
        </RightBox>
      </Container>
    </Section>
  );
};

export default HomePage;
