import React, { useState, useEffect } from "react";
import styled, { keyframes } from "styled-components";

const CarouselContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-around;
  align-items: center;
  position: relative;
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: scale(1.1);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
`;

const fadeOut = keyframes`
  from {
    opacity: 1;
  }
  to {
    opacity: 0;
    transform: scale(0.9);
  }
`;

const CarouselImage = styled.img`
  max-width: 30%;
  border-radius: 10px;
  object-fit: cover;
  position: absolute;
  box-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 1px 3px 1px;
`;

const ImageWrapper = styled.div`
  position: absolute;
  animation-duration: 1s;
  animation-timing-function: ease-in-out;
  animation-fill-mode: forwards;
  transform-origin: center bottom;

  &.in {
    animation-name: ${fadeIn};
    z-index: 1;
  }

  &.out {
    animation-name: ${fadeOut};
    z-index: -1;
  }
`;

const CenterImageWrapper = styled(ImageWrapper)`
  transform: translateX(0);
  opacity: 1;
  max-width: 42%;
`;

const BackImageWrapper = styled(ImageWrapper)`
  opacity: 0.75;
  filter: blur(1px) grayscale(15%) saturate(90%);
`;

const LeftImageWrapper = styled(BackImageWrapper)`
  transform: translate(-11rem, 1rem) rotate(-15deg);
`;

const RightImageWrapper = styled(BackImageWrapper)`
  transform: translate(11rem, 1rem) rotate(15deg);
`;

const Carousel = ({ images }) => {
  const [centerIndex, setCenterIndex] = useState(0);
  const [leftIndex, setLeftIndex] = useState(images.length - 1);
  const [rightIndex, setRightIndex] = useState(1);

  useEffect(() => {
    const intervalID = setInterval(() => {
      setLeftIndex(centerIndex);
      setCenterIndex(rightIndex);
      setRightIndex((rightIndex + 1) % images.length);
    }, 4000);

    return () => clearInterval(intervalID);
  }, [centerIndex, images.length, leftIndex, rightIndex]);

  return (
    <CarouselContainer>
      <LeftImageWrapper className="out">
        <CarouselImage src={images[leftIndex]} />
      </LeftImageWrapper>
      <CenterImageWrapper>
        <CarouselImage src={images[centerIndex]} />
      </CenterImageWrapper>
      <RightImageWrapper className="out">
        <CarouselImage src={images[rightIndex]} />
      </RightImageWrapper>
    </CarouselContainer>
  );
};

export default Carousel;
