import React from 'react';
import styled from 'styled-components';
import DZ from "../assets/dz.svg"
import GB from "../assets/gb.svg.png"
const Container = styled.div`
  position: relative;
`;

const Button = styled.button`
  background-color: transparent;
  border: none;
  color: #fff;
  cursor: pointer;
  font-size: 1rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;

  &:hover {
    background-color: #f2f2f2;
    color: #000;
  }
`;

const Menu = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  z-index: 1;
  background-color: #fff;
  border: 1px solid #ccc;
  border-radius: 5px;
  padding: 0.5rem;
  display: none;

  ${Container}:hover & {
    display: block;
  }
`;

const MenuItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  cursor: pointer;
  padding: 0.5rem 1rem;

  &:hover {
    background-color: #f2f2f2;
  }
`;

const Flag = styled.img`

`;
const LanguagesDropDown = () => {
  return (
    <Container>
      <Button>
        <img src="algerian-flag.png" alt="Algerian flag" width="20" height="20" />
        Arabic
      </Button>
      <Menu>
        <MenuItem>
          <Flag src={DZ} alt="Algerian flag" />
          Arabic
        </MenuItem>
        <MenuItem>
          <Flag src={GB} alt="British flag" />
          English
        </MenuItem>
      </Menu>
    </Container>
  );
};

export default LanguagesDropDown;
