import React from "react";
import styled from "styled-components";
import Flag from "react-flagkit";
import { useTranslation } from 'react-i18next';

const Container = styled.div`
  position: relative;
`;

const Menu = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  z-index: 1;
  background-color: #fff;
  border: 1px solid #ccc;
  border-radius: 5px;
  padding: 0.5rem;
  display: none;

  ${Container}:hover & {
    display: block;
  }
`;

const MenuItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  cursor: pointer;
  padding: 0.5rem 1rem;

  &:hover {
    background-color: #f2f2f2;
  }
`;

const LanguagesDropDown = () => {
  const { t, i18n } = useTranslation();
  return (
    <Container>
      <MenuItem>
        <Flag country={i18n.language === 'ar' ? 'DZ' : 'GB'}andValue />
          {i18n.language === 'ar' ? t('Arabic') : t('English')}
        </MenuItem>

      <Menu>
        <MenuItem
          onClick={() => {
            i18ln.changeLanguage("ar");
          }}
        >
          <Flag country="DZ" />
          Arabic
        </MenuItem>
        <MenuItem
          onClick={() => {
            i18ln.changeLanguage("en");
          }}
        >
          <Flag country="GB" />
          English
        </MenuItem>
      </Menu>
    </Container>
  );
};

export default LanguagesDropDown;
