import React, { useState, useEffect } from "react";
import styled from "styled-components";

import homeIllustration1 from "../assets/screenshot1.jpeg";
import homeIllustration2 from "../assets/screenshot2.jpg";
import homeIllustration3 from "../assets/screenshot3.jpg";

const images = [homeIllustration1, homeIllustration2, homeIllustration3];

const CarouselWrapper = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  background-color: red;
`;

const CarouselItem = styled.img`
max-width: 100%;
object-fit: cover;
  opacity: ${(props) => (props.isActive ? 1 : 0.5)};
  transition: opacity 0.3s ease-in-out;
`;

const HomeCarousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 4000);

    return () => clearInterval(interval);
  }, []);

  const renderCarouselItem = (index) => {
    const isActive = index === activeIndex;
    const isBackLeft =
      index === (activeIndex - 1 + images.length) % images.length;
    const isBackRight = index === (activeIndex + 1) % images.length;

    return (
      <CarouselItem
        key={index}
        src={images[index]}
        isActive={isActive}
        style={{
          transform:
            isActive && !isBackLeft && !isBackRight ? "scale(1.2)" : "scale(1)",
        }}
      />
    );
  };

  return (
    <CarouselWrapper>
      {renderCarouselItem(activeIndex - 1 + images.length)}
      {renderCarouselItem(activeIndex)}
      {renderCarouselItem(activeIndex + 1)}
    </CarouselWrapper>
  );
};

export default HomeCarousel;
