import React, { useState } from 'react';
import styled from 'styled-components';

const CarouselContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 300px;
  width: 100%;
  position: relative;
`;

const CarouselImage = styled.img`
  height: 200px;
  width: 200px;
  margin: 0 10px;
  border-radius: 10px;
  object-fit: cover;
  position: absolute;
  transition: all 0.5s ease;
`;

const CenterImage = styled(CarouselImage)`
  z-index: 1;
  transform: translateX(0);
  opacity: 1;
`;

const BackImage = styled(CarouselImage)`
  z-index: 0;
  opacity: 0.5;
`;

const LeftImage = styled(BackImage)`
  transform: translateX(-150px);
`;

const RightImage = styled(BackImage)`
  transform: translateX(150px);
`;

const HomeCarousel = ({ images }) => {
  const [centerIndex, setCenterIndex] = useState(0);
  const [leftIndex, setLeftIndex] = useState(images.length - 1);
  const [rightIndex, setRightIndex] = useState(1);

  const handleCarousel = (direction) => {
    // update the indexes based on the direction of the carousel
    if (direction === 'left') {
      setCenterIndex((centerIndex - 1 + images.length) % images.length);
      setLeftIndex((leftIndex - 1 + images.length) % images.length);
      setRightIndex((rightIndex - 1 + images.length) % images.length);
    } else {
      setCenterIndex((centerIndex + 1) % images.length);
      setLeftIndex((leftIndex + 1) % images.length);
      setRightIndex((rightIndex + 1) % images.length);
    }
  };

  return (
    <CarouselContainer>
      <LeftImage src={images[leftIndex]} />
      <CenterImage src={images[centerIndex]} />
      <RightImage src={images[rightIndex]} />
      <ArrowButton onClick={() => handleCarousel('left')}>{"<"}</ArrowButton>
      <ArrowButton onClick={() => handleCarousel('right')}>{">"}</ArrowButton>
    </CarouselContainer>
  );
};

export default HomeCarousel;
