import React from 'react';
import styled from 'styled-components';
import Flag from 'react-flagkit';

const Container = styled.div`
  position: relative;
`;

const Button = styled.button`
  background-color: transparent;
  border: none;
  color: #fff;
  cursor: pointer;
  font-size: 1rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;

  &:hover {
    background-color: #f2f2f2;
    color: #000;
  }
`;

const Menu = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  z-index: 1;
  background-color: #fff;
  border: 1px solid #ccc;
  border-radius: 5px;
  padding: 0.5rem;
  display: none;

  ${Container}:hover & {
    display: block;
  }
`;

const MenuItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  cursor: pointer;
  padding: 0.5rem 1rem;

  &:hover {
    background-color: #f2f2f2;
  }
`;


const LanguagesDropDown = () => {
  return (
    <Container>
      <MenuItem>
        <Flag country="" />
          Language
        </MenuItem>
      <Menu>
        <MenuItem onClick={()=> {i18ln.changeLanguage('ar')}}>
        <Flag country="DZ" />
          Arabic
        </MenuItem>
        <MenuItem onClick={()=> {i18ln.changeLanguage('en')}}>
        <Flag country="GB"/>
          English
        </MenuItem>
      </Menu>
    </Container>
  );
};

export default LanguagesDropDown;
