import React from "react";
import styled from "styled-components";
import HeroImg from "../assets/heroIllustration.svg";
import ActionButton from "../components/ActionButton";

const Section = styled.div`
  min-height: ${(props) => `calc(100vh - ${props.theme.navHeight})`};
  background-color: ${(props) => props.theme.body};
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  @media (max-width: 768px) {
    min-height: 100vh;
  }
`;
const Container = styled.div`
  width: 80%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;
const RightBox = styled.div`
  width: 40%;
  display: flex;
  align-items: center;
  justify-content: flex-end;
`;
const HeroImageContainer = styled.img`
  max-width: 100%;
  object-fit: cover;
  @media (max-width: 768px) {
    width: auto;
  }
`;
const LeftBox = styled.div`
  width: 40%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
`;
const Heading = styled.h1`
  width: 80%;
  margin-bottom: 0.5rem;
  font-size: ${(props) => props.theme.fontLargest};
  color: ${(props) => props.theme.primaryColor};
  font-weight: 900;
  text-transform: uppercase;
`;
const SubHeading = styled.h2`
  width: 100%;
  font-size: ${(props) => props.theme.fontxxxl};
  margin-bottom: 2rem;
`;
const Paragraph = styled.p`
  width: 100%;
  font-size: ${(props) => props.theme.fontxl};
  margin-bottom: 1rem;
`;

const InputContainer = styled.div`
  width: 75%;
  display: flex;
  align-items: center;
  border-radius: ${(props) => props.theme.defaultRadius};
  padding: 0.5rem;
 border: 1px solid red;
 margin-bottom: 0.5rem;
`;

const Input = styled.input`
  border: none;
  font-size: 1rem;
  flex: 1;
  margin-right: 0.5rem;

  &:focus {
    outline: none;
  }
`;

const Button = styled.button`
  background-color: ${(props) => props.theme.primaryColor};
  color: #fff;
  border: none;
  border-radius: ${(props) => props.theme.defaultRadius};
  padding: ${(props) => props.theme.actionButtonPadding};
  font-size: ${(props) => props.theme.fontxl};
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
   transform: scale(1.03);
  }
`;

const SmallParagraph = styled.p`
  width: 100%;
  font-size: ${(props) => props.theme.fontmd};
  margin-bottom: 1rem;
`;

const HomePage = () => {
  return (
    <Section>
      <Container>
        <LeftBox>
          <Heading>Grow your Business with our E-commerce solution</Heading>
          <SubHeading>Get more sales and better Inventory Managment</SubHeading>
          <Paragraph>
            Create your online shop, receive orders online from hanuut customers
            base and manage your inventory for free.
          </Paragraph>
          <InputContainer>
            <Input type="email" placeholder="Enter your email address" />
            <Button>Subscribe</Button>
          </InputContainer>
          <SmallParagraph>
            By entering your email, you agree to receive emails from hanuut.
          </SmallParagraph>
        </LeftBox>
        <RightBox>
          <HeroImageContainer src={HeroImg} />
        </RightBox>
      </Container>
    </Section>
  );
};

export default HomePage;
